        String[] ss = s.split("\n");

        for (int i = 0; i < ss.length; i++) {
            for (int j = 0; j < ss[0].length(); j++) {
                if (ss[i].charAt(j) == '@') {
                    int neigh = 0;
                    if (i > 0) {
                        if (ss[i - 1].charAt(j) == '@') {
                            neigh++;
                        }
                    }
                    if (j > 0) {
                        if (ss[i].charAt(j - 1) == '@') {
                            neigh++;
                        }
                    }
                    if (i < ss.length - 1) {
                        if (ss[i + 1].charAt(j) == '@') {
                            neigh++;
                        }
                    }
                    if (j < ss[0].length() - 1) {
                        if (ss[i].charAt(j + 1) == '@') {
                            neigh++;
                        }
                    }

                    if (i > 0 & j > 0) {
                        if (ss[i - 1].charAt(j-1) == '@') {
                            neigh++;
                        }
                    }
                    if (i > 0 && j < ss[0].length() - 1 ) {
                        if (ss[i - 1].charAt(j + 1) == '@') {
                            neigh++;
                        }
                    }
                    if (i < ss.length - 1 && j > 0) {
                        if (ss[i + 1].charAt(j - 1) == '@') {
                            neigh++;
                        }
                    }
                    if (i < ss.length - 1 && j < ss[0].length() - 1) {
                        if (ss[i+1].charAt(j + 1) == '@') {
                            neigh++;
                        }
                    }
                    if (neigh < 4) {
                        k++;
                    }
                }
            }
        }

        System.out.println(k);

